import React from "react";
import { useFormik } from "formik";
import "../styles/Createjar.css";
import { Link, useHistory } from "react-router-dom";
import bottlewnap from "../image/bottlewnap.png";

const initialValues = { jarName: "" };
let history = useHistory();
const onSubmit = (values) => {
  console.log("Form data", values);
  if (values) {
    history.push("/createcookie");
  }
};
const validate = (values) => {
  let errors = {};
  if (!values.jarName) {
    errors.jarName = "Required";
  }
  return errors;
};
function Createjar() {
  const formik = useFormik({
    initialValues,
    onSubmit,
    validate,
  });

  return (
    <>
      <div className="maincreatejar">
        <div className="createjar-left">
          <img src={bottlewnap} alt="bottlewnap" />
        </div>
        <div className="createjar-right">
          <form onSubmit={formik.handleSubmit}>
            <label htmlFor="jarName">Name your Jar</label>
            <input
              type="text"
              placeholder="name of your jar..."
              id="jarName"
              name="jarName"
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              value={formik.values.jarName}
            />
            {formik.errors.jarName ? (
              <div className="error">Required</div>
            ) : null}
            <button type="submit">Next</button>
          </form>
          <Link to="/createcookie"> Create cookies </Link>
        </div>
      </div>
    </>
  );
}
export default Createjar;
