import React from "react";
import { useFormik } from "formik";
import "./Createcookie.css";
import { Link } from "react-router-dom";

function Createcookie() {
  const formik = useFormik({
    initialValues: {
      title: "",
      messageContent: "",
    },
  });

  return (
    <>
      <div className="maincreatecookie">
        <form onSubmit={formik.handleSubmit}>
          <label htmlFor="message-title">Message Title</label>
          <input
            type="text"
            id="message-title"
            name="message-title"
            onChange={formik.handleChange}
            value={formik.values.title}
          />
          <label htmlFor="select-jar">Select a Jar</label>
          <select name="select-jar" id="select-jar">
            <option value="jar1">Jar 1</option>
            <option value="jar2">Jar 2</option>
            <option value="jar3">Jar 3</option>
          </select>
          <label htmlFor="text-content">Write your message</label>
          <textarea
            name="text-content"
            id="text-content"
            onChange={formik.handleChange}
            value={formik.values.messageContent}
            cols="30"
            rows="10"
          ></textarea>
          <button type="submit">
            <Link to="/shelf">Create</Link>
          </button>
        </form>
      </div>
    </>
  );
}

export default Createcookie;
