import React, { useState, useEffect } from "react";
import { useParams, useHistory } from "react-router-dom";
import axios from "axios";
import "../styles/ReadList.css";

function ReadList() {
  const [cookieData, setCookieData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(false);
  const { id } = useParams();
  const getCookieDataAPI = `http://localhost:5000/cookies/${id}`;
  const deleteCookieDataAPI = `http://localhost:5000/shelf/cookies/delete/${id}`;
  const history = useHistory();

  useEffect(() => {
    axios
      .get(getCookieDataAPI)
      .then((response) => {
        setCookieData(response.data);
        setIsLoading(false);
      })
      .catch((err) => {
        console.log(err);
        setError(true);
      });
  }, [getCookieDataAPI]);

  const deleteCookie = () => {
    setIsLoading(true);
    axios
      .delete(deleteCookieDataAPI)
      .then((res) => {
        setIsLoading(false);
        history.push(`/shelf/${res.data.jarID}`);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <div className="mainreadlist">
      {isLoading ? (
        <div>Loading...</div>
      ) : error ? (
        <div>Error</div>
      ) : (
        <div>
          {cookieData
            .filter((cookie) => cookie.read === true)
            .map((val, id) => {
              <div key={id}>
                <h3>{val.cookieTitle}</h3>
                <img src={val.cookieImage} alt="img" />
                <span>{val.cookieContent}</span>
              </div>;
            })}
        </div>
      )}
    </div>
  );
}

export default ReadList;
