import { GET_JAR, CREATE_JAR } from "../actionTypes/index";
import * as api from "../../api/index";

export const getJars = () => async (dispatch) => {
  try {
    const { data } = await api.getJars();
    dispatch({ type: GET_JAR, payload: data });
  } catch (error) {
    console.log("ERROR:", error);
  }
};

export const createJar = (jar) => async (dispatch) => {
  try {
    const { data } = await api.createJar(jar);
    dispatch({ type: CREATE_JAR, payload: data });
  } catch (error) {
    console.log("ERROR:", error);
  }
};
