import { GET_JAR, CREATE_JAR } from "../actionTypes/index";

const jarReducer = (jars = [], action) => {
  switch (action.type) {
    case GET_JAR:
      return action.payload;
    case CREATE_JAR:
      return [...jars, action.payload];
    default:
      return jars;
  }
};

export default jarReducer;
