import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import * as api from "../../api/index";

export const getJars = createAsyncThunk("Jar/getJars", async () => {
  const { data } = await api.getJars;
  return data;
});

export const createJar = createAsyncThunk("Jar/createJar", async () => {
  const { data } = api.createJar;
  return data;
});

const jarSlice = createSlice({
  name: "Jar",
  initialState: {
    jars: [],
    jarName: "",
    status: null,
  },
  reducers: {},
  extraReducers: {
    [getJars.pending](state) {
      state.status = "loading";
    },
    [getJars.fulfilled](state, action) {
      state.status = "success";
      state.jars = action.payload;
    },
    [getJars.rejected](state) {
      state.status = "failed";
    },
    [createJar.fulfilled](state, action) {
      state.status = "success";
      const newJar = action.payload;
      state.jars.push(newJar);
    },
  },
});

export default jarSlice.reducer;
