import { getType, getJars, createJar } from "../actions";
import { INIT_STATE } from "../constants";

const jarReducer = (state = INIT_STATE, action) => {
  switch (action.type) {
    case getType(getJars.getJarsRequest):
      return {
        ...state,
        isLoading: true,
      };
    case getType(getJars.getJarsRequest):
      return {
        ...state,
        isLoading: false,
        data: action.payload,
      };
    case getType(getJars.getJarsRequest):
      return {
        ...state,
        isLoading: false,
      };
    case getType(createJar.createJarRequest):
      return {
        ...state,
        data: [...state.jars.data, action.payload],
      };
    default:
      return state;
  }
};

export default jarReducer;
