import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import axios from "axios";
import Modal from "react-modal";
import CreateCookie from "../components/CreateCookie";
import "../styles/Jar.css";

function Jar() {
  const [cookieForm, setCookieForm] = useState(false);

  const [jarData, setJarData] = useState([]);
  const [cookieData, setCookieData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(false);
  const { id } = useParams();

  const fetchData = () => {
    const jarDataAPI = `http://localhost:5000/shelf/${id}`;
    const cookieDataAPI = "http:/localhost:5000/cookies";

    const getJarData = axios.get(jarDataAPI);
    const getCookieData = axios.get(cookieDataAPI);

    axios
      .all(getJarData, getCookieData)
      .then(
        axios.spread((...allData) => {
          setJarData(allData[0].data);
          setCookieData(allData[1].data);
          setIsLoading(false);
        })
      )
      .catch((error) => {
        console.log(error);
        setError(true);
      });
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="mainjar">
      {isLoading ? (
        <div>Loading...</div>
      ) : error ? (
        <div>Error...</div>
      ) : (
        <div>
          <h1>{jarData.jarName}</h1>
          {cookieData.map((val, id) => {
            return (
              <div>
                <h2>{val.cookieTitle}</h2>
                <p>{val.cookieContent}</p>
              </div>
            );
          })}
        </div>
      )}
      <button
        onClick={() => {
          setCookieForm(true);
        }}
      >
        Create cookie
      </button>
      <Modal
        isOpen={cookieForm}
        onRequestClose={() => {
          setCookieForm(false);
        }}
      >
        <CreateCookie />
        <button
          onClick={() => {
            setCookieForm(false);
          }}
        >
          X
        </button>
      </Modal>
    </div>
  );
}

export default Jar;
