import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import axios from "axios";
import Modal from "react-modal";
import CreateCookie from "../components/CreateCookie";
function Jar() {
  const [cookieForm, setCookieForm] = useState(false);

  const [jarData, setJarData] = useState([]);
  const { id } = useParams();
  const URL = `http://localhost:5000/shelf/${id}`;
  useEffect(() => {
    axios.get(URL).then((response) => {
      setJarData(response.data);
      console.log(response);
    });
  }, [URL]);
  return (
    <div>
      <h1>{jarData.jarName}</h1>
      <button
        onClick={() => {
          setCookieForm(true);
        }}
      >
        Create cookie
      </button>
      <Modal
        isOpen={cookieForm}
        onRequestClose={() => {
          setCookieForm(false);
        }}
      >
        <CreateCookie />
        <button
          onClick={() => {
            setCookieForm(false);
          }}
        >
          X
        </button>
      </Modal>
    </div>
  );
}

export default Jar;
