import { takeLatest, put, call } from "redux-saga/effects";
import * as actions from "../actions/index";
import * as api from "../../api";

function* getJarsSaga(action) {
  try {
    const jars = yield call(api.getJars());
    yield put(actions.getJars.getJarsSuccess(jars.data));
  } catch (error) {
    console.log("ERROR:", error);
    yield put(actions.getJars.getJarsFailure(error));
  }
}

function* createJarSaga(action) {
  try {
    const jar = yield call(api.createJar());
    yield put(actions.createJar.createJarSuccess(jar.data));
  } catch (error) {
    console.log("ERROR:", error);
    yield put(actions.createJar.createJarFailure(error));
  }
}

function* mySaga() {
  yield takeLatest(actions.getJars.getJarsRequest, getJarsSaga);
  yield takeLatest(actions.createJar.createJarRequest, createJarSaga);
}

export default mySaga;
