import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import axios from "axios";
import Modal from "react-modal";
import CreateCookie from "../components/CreateCookie";
import "../styles/Jar.css";
function Jar() {
  const [cookieForm, setCookieForm] = useState(false);

  const [jarData, setJarData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(false);
  const { id } = useParams();
  const URL = `http://localhost:5000/shelf/${id}`;
  useEffect(() => {
    axios
      .get(URL)
      .then((response) => {
        setJarData(response.data);
        console.log(response);
        setIsLoading(false);
      })
      .catch((error) => {
        console.log(error);
        setError(true);
      });
  }, [URL]);
  return (
    <div className="mainjar">
      if(isLoading) {<div>Loading...</div>} elseif(error){<div>Error</div>}else{" "}
      {<div>{jarData.jarName}</div>}}
      <button
        onClick={() => {
          setCookieForm(true);
        }}
      >
        Create cookie
      </button>
      <Modal
        isOpen={cookieForm}
        onRequestClose={() => {
          setCookieForm(false);
        }}
      >
        <CreateCookie />
        <button
          onClick={() => {
            setCookieForm(false);
          }}
        >
          X
        </button>
      </Modal>
    </div>
  );
}

export default Jar;
